import React, {useState} from 'react';
// to manage the state where the drawer is opened or not we need to import >userState
import { Link } from 'react-router-dom';

import MainHeader from './MainHeader';
import NavLinks from './NavLinks';
import SideDrawer from './SideDrawer';

import './MainNavigation.css';


const MainNavigation = props => {
    return( 
        <React.Fragment>
        {/* with JS,we can have just one route Jsx element per component, so r.fragment wrapper allow this requirement of having one root element and then in there, we can several elements side by side/nested    */}
            <SideDrawer>
                <nav className="main-navigation__drawer-nav">
                <NavLinks/>
                </nav>
            </SideDrawer>
            <MainHeader>
                <button className="main-navigation__menu-btn">
                    <span/>
                    <span/>
                    <span/>
                </button>
                <h1 className="main-navigation__title">
                    <Link to="/">My Holidrinks</Link>
                </h1>
                <nav className="main-navigation__header-nav">
                    <NavLinks/>
                </nav>
            </MainHeader>
        </React.Fragment>
    )
};

export default MainNavigation;
