import React from 'react';
import {useParams} from 'react-router-dom';
// to find place by id I use> useParams
import EventList from '../components/EventList';

const DUMMY_EVENTS =[
{
    id:'p1',
    title:'Basement Theatre',
    description: 'Here since 2 days, mood to visit a bit the city white other buddies',
    imageUrl:'<a href="https://imgur.com/mfaCZoJ"><img src="https://i.imgur.com/mfaCZoJ.jpg" title="source: imgur.com" /></a>',
    address:'Lower Greys Avenue, Auckland CBD, Auckland 1010, New Zealand',
    location:{
        lat:40.7484405,
        lng:-73.9856644,
    },
    creator:'u1'
},
{
    id:'p2',
    title:'Empire State Building',
    description: 'One of the most famous sky scrapers',
    imageUrl:'https://bit.ly/38e2HXt',
    address:'20 W 34th St, New York, NY 10001, United States',
    location:{
        lat:40.7484405,
        lng:-73.9856644,
    },
    creator:'u2'
},
];

const UserEvents= () => {
    const userId= useParams().userId;
    // use params return an object which has a dynamic segments set up in the route, so the > :userId from app.js, for example as properties
    const loadedEvents = DUMMY_EVENTS.filter(event =>event.creator === userId);
    // that will load events by user
    return <EventList items={loadedEvents}/>
};

export default UserEvents;
